import React, {Component} from 'react';
// import logo from './logo.svg';
import Titlebar from './components/Titlebar/Titlebar';
import Rolled from './components/Rolled/Rolled';
import './App.css';
import { Container, Row, Col } from 'react-bootstrap';

const initialState = {
    route: 'home',
    theme: 'light'
}

class App extends Component{
  constructor() {
    super()
    this.state = this.initialState;
    this.rule = {
      num :'###',
      text : "drink 3 sips"
    }
  }

  onRouteChange = (route) => {
    this.setState({
      route: route,
    })
  }

  // toggleTheme = (theme) => {
  //   if (theme === 'light') {
  //     this.setState({
  //       theme: 'dark'
  //     })
  //   } else {
  //     this.setState({
  //       theme: 'light'
  //     })
  //     particlesOptions.particles.color = '#000000';
  //   }
  // }

  render() {
    return (
        <Container className="App">
          <Row>
            <Col><Titlebar /></Col>
          </Row>
          <Row>
          <Col><Rolled num={this.rule.num} text={this.rule.text}/></Col>
          </Row>
        </Container>
    );
  }
}

export default App;
