import React from 'react';
import './BigRedButton.css'
import { Container, Row, Col, Button } from 'react-bootstrap';

const BigRedButton = ({onRoll, num}) => {
  return (
    <Container>
      <Row className="justify-content-center">
        <Col xs={8}> // style={{height: '18rem'}}  className="mt-4 border border-button rounded-circle bigButton">
          {/* <div classname="h-100 w-100" onClick={() => onRoll({ num })}> */}
            {num}
          <Button type={"button"} className="btn btn-default btn-circle"><i class="fa fa-check"></i>
                            </Button>
        </Col>
      </Row>
    </Container>
  );
}

export default BigRedButton;
