import React, {Component} from 'react';
// import logo from './logo.svg';
import Titlebar from './components/Titlebar/Titlebar';
import Rolled from './components/Rolled/Rolled';
import BigRedButton from './components/BigRedButton/BigRedButton';
import './App.css';
import { Container, Row, Col } from 'react-bootstrap';

const initialState = {
  rule: {
    num: 123,
    text: "not a rule"
  }
}

const rulesList = {
  2568: {
    num: 2568,
    text: "drink 3 sips"
  }
}

class App extends Component{
  constructor() {
    super()
    this.state = initialState;
  }

  onRoll = ({ num }) => {
    var newNum = Math.ceil(Math.random() * num);
    var newText = ""
    if (newNum === 1) {
      newText = "YOU LOSE";
    }
    if (rulesList[newNum]) {
      newText = rulesList[newNum];
    } else {
      this.setState({
        rule: {
          num: newNum,
          text: "drink 4 sips"
        }
      })
    }

  }

  render() {
    return (
        <Container className="App">
          <Row>
            <Col><Titlebar /></Col>
          </Row>
          <Row>
          <Col><Rolled num={this.state.rule.num} text={this.state.rule.text}/></Col>
          </Row>
          <Row>
          <Col><BigRedButton num={this.state.rule.num} onRoll={this.onRoll}/></Col>
          </Row>
        </Container>
    );
  }
}

export default App;
