import React, {Component} from 'react';
// import logo from './logo.svg';
import Titlebar from './components/Titlebar/Titlebar';
import Rolled from './components/Rolled/Rolled';
import Button from './components/Button/Button';
import './App.css';
import { Container, Row, Col } from 'react-bootstrap';

// const initialState = {
//     route: 'home',
// }

// const rules = {
//   2568: {
//     num: 2568,
//     text: "drink 3 sips"
//   }
// }

class App extends Component{
  constructor() {
    super()
    this.state = {
      route: 'home',
    };
    this.rule = {
      num: '###',
      text: "rules display here"
    };
  }

  onRouteChange = (route) => {
    this.setState({
      route: route,
    })
  }

  render() {
    return (
        <Container style={{width: '100%'}} className="App">
          <Row>
            <Col><Titlebar /></Col>
          </Row>
          <Row>
            <Col><Rolled num={this.rule.num} text={this.rule.text}/></Col>
          </Row>
          <Row>
            <Col><Button num={this.rule.num}/></Col>
          </Row>
        </Container>
    );
  }
}

export default App;
