import React from 'react';
import { FormControl, Row, Col, InputGroup, Button } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import './Players.css'

class Players extends React.Component {
  constructor() {
    super();
    this.state = {
      inputName: ''
    }
  }

  playerList = ["dan", "maddie", "julia", "jaime", "jer", "hayden", "tat", "sam", "julia kim"]

  render() {
    return (
      <div>
        <Row className="justify-content-center mb-2">
          <Col xs={10}>
          <InputGroup>
            <FormControl className="playerEntry" type="name" placeholder="new player" />
            <InputGroup.Append>
              <Button variant="dark" onClick={() => {playerList.push()}}><FontAwesomeIcon icon="plus" className="pt-1"/></Button>
            </InputGroup.Append>
          </InputGroup>
          </Col>
        </Row>
        <>
          {/* {playerList.map(player => (<div className="player" key={player}>{player}</div>))} */}
          {playerList.join(" - ")}
        </>
      </div>
    );
  }
  
}

export default Players;
