import React from 'react';
import NumWindow from '../NumWindow/NumWindow'
import { Container, Row, Col, Button } from 'react-bootstrap';
import './BigRedButton.css'

const BigRedButton = ({onRoll, num, formatValue}) => {
  return (
    <Container>
      <Row className="justify-content-center">
        <Col className="mt-4">
          <Button className="btn btn-default btn-circle bigButton disable" onClick={() => onRoll(num)}>
            <NumWindow formatValue={formatValue} num={num}/>
          </Button>
        </Col>
      </Row>
    </Container>
  );
}

export default BigRedButton;
