import React, {Component} from 'react';
// import logo from './logo.svg';
import Titlebar from './components/Titlebar/Titlebar';
import Rolled from './components/Rolled/Rolled';
import BigRedButton from './components/BigRedButton/BigRedButton';
import './App.css';
import { Container, Row, Col } from 'react-bootstrap';

const initialState = {
  rule: {
    num: 123,
    text: "not a rule"
  }
}

const rules = {
  2568: {
    num: 2568,
    text: "drink 3 sips"
  }
}

class App extends Component{
  constructor() {
    super()
    this.state = initialState;
  }

  onRoll = ({ num }) => {
    this.setState({
      rule : {
        num: 456,
        text: "new rule"
      }
    })
  }

  render() {
    return (
        <Container className="App">
          <Row>
            <Col><Titlebar /></Col>
          </Row>
          <Row>
          <Col><Rolled num={this.state.rule.num} text={this.state.rule.text}/></Col>
          </Row>
          <Row>
          <Col><BigRedButton num={this.state.rule.num} onRoll={this.onRoll}/></Col>
          </Row>
        </Container>
    );
  }
}

export default App;
