import React, {Component} from 'react';
// import logo from './logo.svg';
import Titlebar from './components/Titlebar/Titlebar';
import Rolled from './components/Rolled/Rolled';
import BigRedButton from './components/BigRedButton/BigRedButton';
import './App.css';
import { Container, Row, Col } from 'react-bootstrap';

const initialState = {
  ruleNum: 123,
  ruleText: "not a rule",
  rolling: false,
  gameOver: false,
  dupe: 2
}

const rulesList = [{
    num: 69,
    text: "lock arms and drink"
  },{
    num: 420,
    text: "blaze it"
  },{
    num: 17,
    text: "naked mile"
  }
]
  
class App extends Component{
  constructor() {
    super()
    this.state = initialState;
  }

  formatValue = (value) => value.toFixed(0);

  rollNewNum = (num) => {
    var newNum = 0;
    if (num === 1) {
      newNum = 500;
      this.setState({gameOver:false})
    } else {
      newNum = Math.ceil(Math.random() * num);
    }
    return newNum;
  }

  setNewText = (newNum) => {
    var newText = "drink"
    if (newNum === 1) {
      newText = "YOU LOSE";
    } else {
      for (var i = 0; i < rulesList.length; i++) {
        if (rulesList[i].num === newNum) {
          newText = rulesList[i].text;
        }
      }
    }
    return newText;
  }

  onRoll = (num) => {
    var newNum = this.rollNewNum(num);
    var newText = this.setNewText(newNum);
    var stateTimeout = 1000
    
    
    if (newNum === num) {
      newText += (" X" + this.state.dupe)
      this.setState({dupe: (this.state.dupe) + 1})
      stateTimeout = 0;
    } else {
      this.setState({dupe:2});
    }
    
    this.setState({
      ruleNum: newNum,
      rolling: true
    })

    var newState = {
      ruleText: newText,
      rolling: false,
    }
    if (newNum === 1) {
      newState.gameOver = true;
    }
    console.log(newState);
    var self = this;
    setTimeout(function () { self.setRolledRule(newState); }, stateTimeout);
  }

  setRolledRule = (newState) => {
    this.setState(newState);
  }

  render() {
    return (
        <Container className="App">
          <Row>
            <Col><Titlebar /></Col>
          </Row>
          <Row>
          <Col><BigRedButton num={this.state.ruleNum} onRoll={this.onRoll} formatValue={this.formatValue} gameOver={this.state.gameOver}/></Col>
          </Row>
          <Row>
            <Col><Rolled text={this.state}/></Col>
          </Row>
        </Container>
    );
  }
}

export default App;
