import React from 'react';
import { FormControl, Row, Col, InputGroup, Button } from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import './Players.css'

let playerList = []
  
class Players extends React.Component {
  constructor() {
    super();
    this.state = {
      inputName: '',
      playerList: playerList
    }
  }

  onNameChange = (event) => {
    this.setState({inputName: event.target.value})
  }

  addName = () => {
    if (this.state.inputName !== '') {
      playerList.push(this.state.inputName)
      this.setState({inputName: '', playerList: playerList})
    }
  }

  render() {
    return (
      <div>
        <Row className="justify-content-center mb-2">
          <Col xs={10}>
          <InputGroup>
              <FormControl className="playerEntry" type="name" placeholder="new player" value={this.state.inputName} onChange={this.onNameChange}/>
            <InputGroup.Append>
              <Button variant="dark" onClick={this.addName}><FontAwesomeIcon icon="plus" className="pt-1"/></Button>
            </InputGroup.Append>
          </InputGroup>
          </Col>
        </Row>
          {playerList.join(" - ")}
      </div>
    );
  }
  
}

export default Players;
