import React, {Component} from 'react';
// import logo from './logo.svg';
import Titlebar from './components/Titlebar/Titlebar';
import Rolled from './components/Rolled/Rolled';
import BigRedButton from './components/BigRedButton/BigRedButton';
import './App.css';
import { Container, Row, Col } from 'react-bootstrap';

// const initialState = {
//     route: 'home',
// }

// const rules = {
//   2568: {
//     num: 2568,
//     text: "drink 3 sips"
//   }
// }

class App extends Component{
  constructor() {
    super()
    this.state = {
      route: 'home',
    };
    this.rule = {
      num: 123,
      text: "rules display here"
    };
  }

  onRoll = ({num}) => {
    alert(num);
  }

  onRouteChange = (route) => {
    this.setState({
      route: route,
    })
  }

  render() {
    return (
        <Container className="App">
          <Row>
            <Col><Titlebar /></Col>
          </Row>
          <Row>
          <Col><Rolled num={this.rule.num} text={this.rule.text}/></Col>
          </Row>
          <Row>
          <Col><BigRedButton num={this.rule.num} onRoll={this.onRoll}/></Col>
          </Row>
        </Container>
    );
  }
}

export default App;
